<template>
  <ion-modal :is-open="showPopup">
    <ion-header>
      <ion-toolbar>
        <ion-title>Chọn món</ion-title>
      </ion-toolbar>
    </ion-header>
    <ion-content>
      <!-- Slide of images -->
      <ion-slides pager="true" options="slideOptions">
        <ion-slide
          pager="true"
          options="slideOptions"
          v-for="(image, index) in images"
          :key="index"
        >
          <img class="img-oder" :src="image.url" :alt="'Slide ' + (index + 1)" />
        </ion-slide>
      </ion-slides>

      <!-- Input field -->
      <ion-input class="inputData" v-model="inputData" placeholder="Enter data"></ion-input>

      <!-- Buttons -->
      <ion-button class="closePopup" @click="closePopup">Cancel</ion-button>
      <ion-button class="submitData" @click="submitData">Submit</ion-button>
    </ion-content>
  </ion-modal>
</template>

<script lang="ts">
import { IonModal, IonicSlides, IonInput, IonButton } from "@ionic/vue";
import { defineComponent, ref } from "vue";

export default defineComponent({
  components: {
    IonModal,
    IonicSlides,
    IonInput,
    IonButton,
  },
  props: {
    showPopup: Boolean,
  },
  emits: ["close", "submit"],
  setup(props, { emit }) {
    const images = [
      {
        url: "data:image/jpeg;base64,",
      },
    ];
    const inputData = ref("");
    const slideOptions = {
      slidesPerView: 1,
      spaceBetween: 20, 
    };

    const closePopup = () => {
      emit("close");
      inputData.value = ''
    };

    const submitData = () => {
      emit("submit", inputData.value);
      closePopup();
    };

    return {
      slideOptions,
      images,
      inputData,
      closePopup,
      submitData,
    };
  },
});
</script>
<style scoped>
.img-oder {
  width: 360px;
  padding: 10px;
  margin: 10px;
  border-radius: 10px;
}

.inputData {
  padding: 20px;
  margin: 20px;
}
.closePopup {
  margin-left: 20px;
  margin-right: 10px;
}
</style>
